var a;
a=org.jsoup.Jsoup.parse(result.replace(/javascript:Chapter\((\d+)\,(\d+)\)\;/g,'/read/$2/$1/'));
var b=a.select('dd>a').toArray();
for (var i=0;i<b.length;i=i+3){
   c=i+2;
if(c<b.length){
   var t;
   t=b[i];
   b[i]=b[i+2];
   b[i+2]=t;
}else{
   c=i+1;
   if(c<b.length){
     var t;
     t=b[i];
     b[i]=b[i+1];
     b[i+1]=t;
}
}
}
var list=[];
list=b.map(x=>({n:x.text(),u:x.attr('href')}));
list
</js>

